class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        map<int,set<int>> m;
        set<int> s;
        int maxi=0;
        for(auto q: queries){
            maxi=max(q[1],maxi);
        }
        m[maxi].insert(0);
        vector<bool> ans;
        s.insert(maxi);
        s.insert(0);
        for(auto q: queries){
            if(q[0]==1){
                if(q[1]==maxi|| q[1]==0){
                    continue;
                }
                auto it=s.lower_bound(q[1]);
                auto it2=it;
                auto it1=--it;
                int num1=*it1;
                int num2=*it2;
                if(m.find(num2-num1)!=m.end()){
                    m[num2-num1].erase(num1);
                    if(m[num2-num1].size()==0){
                        m.erase(num2-num1);
                    }
                }
                m[num2-q[1]].insert(q[1]);
                m[q[1]-num1].insert(num1);
                s.insert(q[1]);
            }   
            else{
                if(q[1]<q[2]){

                    ans.push_back(false);
                    continue;
                }
                int flag=0;
                auto it=m.lower_bound(q[2]);
                for(;it!=m.end();it++)
                {

                    auto its=(it->second).lower_bound(q[1]-q[2]);
                    if((*its)==q[1]-q[2]){
                        ans.push_back(true);
                        flag=1;
                        break;
                    }
                    if(its!=(it->second).begin()){
                         --its;
                         if((*its)<=q[1]){
                            ans.push_back(true);
                            flag=1;
                            break;
                         }
                    }
                }
                if(flag==0){
                    ans.push_back(false);
                }
            }
        }

        return ans;
    }
};
