class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        map<int, int> obstacles;

        //set<int> space;

        vector<bool> ans;
        for(int i = 0; i < queries.size(); i++) {
            if(queries[i][0] == 1) {
                if(obstacles.size() == 0) 
                    obstacles[queries[i][1]] = queries[i][1];
                else {
                    auto it = obstacles.lower_bound(queries[i][1]);

                    if(it == obstacles.begin()) {
                        obstacles[queries[i][1]] = queries[i][1];
                    }
                    else {
                        it--;
                        obstacles[queries[i][1]] = max(it->second, queries[i][1] - it->first);
                        it++;
                        it++;
                    }
                    int mx = obstacles[queries[i][1]], prev = queries[i][1];

                    for(; it != obstacles.end(); it++) {                    
                        mx = max(mx, it->first - prev);
                        if(mx == it->second)
                            break;
                        it->second = mx;
                        prev = it->first;
                    }
                }  
            }

            else if(queries[i][1] < queries[i][2])
                ans.push_back(false);
            else {
                auto it = obstacles.lower_bound(queries[i][1]);
                if(it == obstacles.begin()) 
                    ans.push_back(true);
                else {
                    it--;
                    int mx = max(it->second, queries[i][1] - it->first);
                    if(mx >= queries[i][2])
                        ans.push_back(true);
                    else
                        ans.push_back(false);
                }             
            }
        }
        return ans;
    }
};
