class Solution {
public:
int minn[300],maxx[300];
int cur[300];
int vis[60000];
inline void update(int pos){
    int begin1=pos*250;
    int end1=pos*250+250;
    int cur1 = begin1;
    int ans=1;
    for(int i=begin1;i<=end1;i++){
        if(vis[i]==1){
            ans=max(ans,i-cur1);
            cur1=i;
        }
    }
    ans = max(ans,end1-cur1);
    cur[pos]=ans;
}
inline void deal(int pos,int pre,int final,int &ans){
    int begin1=pos*250;
    for(int i=begin1;i<=final;i++){
        if(vis[i]==1){
            ans=max(ans,i-pre);
            pre=i;
        }
    }
    ans = max(ans,final-pre);
}
vector<bool> getResults(vector<vector<int>>& queries) {
    memset(vis,0,sizeof(vis));
    for(int i=0;i<250;i++){
        cur[i]=250;
        minn[i]=-1;
        maxx[i]=-1;
    }
    vector<bool> ret;
    for(int i=0;i<queries.size();i++){
        if(queries[i][0]==1){
            int pos = queries[i][1]/250;
            if(minn[pos]==-1){
                minn[pos]=queries[i][1];
                maxx[pos]=queries[i][1];
            }else{
                minn[pos]=min(minn[pos],queries[i][1]);
                maxx[pos]=max(maxx[pos],queries[i][1]);
            }
            vis[queries[i][1]]=1;
            update(pos);
        }
        if(queries[i][0]==2){
            int ans=-1;
            int pos = queries[i][1]/250;
            int pre=0;
            for(int i=0;i<=pos-1;i++){
                if(minn[i]==-1) continue;
                ans = max(ans,minn[i]-pre);
                pre = minn[i];
                ans = max(ans,cur[i]);
                pre = maxx[i];
            }
            deal(pos,pre,queries[i][1],ans);
            if(ans>=queries[i][2]) ret.push_back(true);
            else ret.push_back(false);
        }
    }
    return ret;
}
};
