class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& q) {
        map<int,int>mp;
        mp[0]=0;
        vector<bool>ans;
        for(int x=0;x<q.size();x++){
            if(q[x].size()==2){
                mp[q[x][1]]++;
                auto it=mp.find(q[x][1]);
                it--;
                mp[q[x][1]]=max(it->second,q[x][1]-it->first);
                it++;
                it++;
                while(it!=mp.end()){
                    auto tm=it;
                    tm--;
                    int mx=max(tm->second,it->first-tm->first);
                    if(mx==it->second)break;
                    else{
                        it->second=mx;
                        it++;
                    }
                }
            }else{
                int mx=0;
                // int border=q[x][1];
                // int pr=0;
                // for(auto it=mp.begin();it!=mp.end();it++){
                //     if(it->first>border)break;
                //     mx=max(mx,it->first-pr);
                //     pr=it->first;
                // }
                if(mp.find(q[x][1])!=mp.end()){
                    auto it=mp.find(q[x][1]);
                    it--;
                    mp[q[x][1]]=max(it->second,q[x][1]-it->first);
                    mx=mp[q[x][1]];
                }else{
                mp[q[x][1]]++;
                auto it=mp.find(q[x][1]);
                it--;
                mx=max(it->second,q[x][1]-it->first);

                mp.erase(q[x][1]);
                }
                // mx=max(mx,border-pr);
                if(mx>=q[x][2])ans.push_back(true);
                else ans.push_back(false);
            }
        }
        return ans;
    }
};
