class Solution:
    def getResults(self, query: List[List[int]]) -> List[bool]:
        tmp = [0]

        def passs(strt, end, pt, sz):
            vidi = pt
            if end < len(tmp) and tmp[end] <= pt:
                vidi = tmp[end]         
            mid = strt + (end - strt) // 2
            if (end - mid) == 1 and (vidi - tmp[mid]) >= sz:
                return True
            if vidi - tmp[mid] > (end - mid)*(sz - 1):
                return True
            hola = False
            if vidi - tmp[mid] - (end - mid - 1) >= sz:
                hola |= passs(mid, end, pt, sz)
            if not hola and tmp[mid] - tmp[strt] - (mid - strt - 1) >= sz:
                hola |= passs(strt, mid, pt, sz)

            return hola


        def fnd(pt, sz):
            idx = bisect_left(tmp, pt)

            return passs(0, idx, pt, sz)

            return False

        ans = []

        for q in query:
            # print(tmp)
            if len(q) == 2:
                insort(tmp, q[1])
            else:
                _, pt, sz = q
                ans.append(fnd(pt, sz))

        return ans
