class Solution {
public:
    vector<long> segT;

    void build(int s, int e, int i, std::vector<long>& a) {
        if (s == e) {
            segT[i] = a[s];
            return;
        }
        int mid = s + (e - s) / 2;
        build(s, mid, 2 * i + 1, a);
        build(mid + 1, e, 2 * i + 2, a);
        segT[i] = std::max(segT[2 * i + 1], segT[2 * i + 2]);
    }

    void update(int s, int e, int i, int q, long v) {
        if (s == e) {
            segT[i] = v;
            return;
        }
        int mid = s + (e - s) / 2;
        if (q <= mid)
            update(s, mid, 2 * i + 1, q, v);
        else
            update(mid + 1, e, 2 * i + 2, q, v);
        segT[i] = std::max(segT[2 * i + 1], segT[2 * i + 2]);
    }

    long range(int s, int e, int i, int qs, int qe) {
        if (qs <= s && qe >= e)
            return segT[i];
        if (qs > e || qe < s)
            return LONG_MIN;
        int mid = s + (e - s) / 2;
        long l = range(s, mid, 2 * i + 1, qs, qe);
        long r = range(mid + 1, e, 2 * i + 2, qs, qe);
        return std::max(l, r);
    }

    std::vector<bool> getResults(std::vector<std::vector<int>>& queries) {
        std::vector<bool> arr;
        std::set<int> set;
        set.insert(0);
        set.insert(100000);
        std::unordered_map<int, int> map;
        map[0] = 0;
        map[100000] = 100000;
        segT.resize(5 * static_cast<int>(1e5), LONG_MIN);

        for (const auto& i : queries) {
            if (i[0] == 1) {
                set.insert(i[1]);
                int prev = *std::prev(set.lower_bound(i[1]));
                int next = *set.upper_bound(i[1]);
                map[next] = next - i[1];
                map[i[1]] = i[1] - prev;
                update(0, 100000, 0, i[1], map[i[1]]);
                update(0, 100000, 0, next, map[next]);
            } else {
                int prev = *std::prev(set.lower_bound(i[1]));
                if (i[1] - prev >= i[2]) {
                    arr.push_back(true);
                    continue;
                } else if (range(0, 100000, 0, 0, i[1]) >= i[2]) {
                    arr.push_back(true);
                    continue;
                }
                arr.push_back(false);
            }
        }
        return arr;
    }
};
