#define lli long long
class Solution {
public:
    void update(lli ind, lli value, lli i, lli l, lli r, vector<lli> &seg)
{
    if (l == r)
    {
        seg[i] = value;
        return;
    }
    lli mid = (l + r) / 2;
    if (ind <= mid)
    {
        update(ind, value, 2 * i + 1, l, mid, seg);
    }
    else
    {
        update(ind, value, 2 * i + 2, mid + 1, r, seg);
    }
    seg[i] = max(seg[2 * i + 1], seg[2 * i + 2]);
    return;
}
    lli rsum(lli left, lli right, lli i, lli l, lli r, vector<lli> &seg)
{
    lli mid = (l + r) / 2;
    if (l >= left && r <= right)
    {
        return seg[i];
    }
    if (l > right || r < left)
    {
        return -1e9;
    }
    // lli a=update(left, right, value, 2 * i + 1, l, mid, seg, seg1);
    // lli b=update(left, right, value, 2 * i + 2, mid + 1, r, seg, seg1);
    return max(rsum(left, right, 2 * i + 1, l, mid, seg),rsum(left, right, 2 * i + 2, mid + 1, r, seg));
}
    vector<bool> getResults(vector<vector<int>>& queries) {
        int n=5*10000+10;
        vector<lli>seg(4*n,-1e9);
        set<lli>st;
        st.insert(0);
        lli q=queries.size();
        vector<bool>ans;
        update(0,1e9,0,0,n-1,seg);
        for(lli i=0;i<q;i++){
            if(queries[i][0]==1){
                lli x=queries[i][1];
                st.insert(x);
                auto it=st.find(x);
                if(it!=st.begin()){
                    auto it1=it;
                    --it1;
                    update(*it1,x-*it1,0,0,n-1,seg);
                }
                if(it!=--st.end()){
                    auto it1=it;
                    it1++;
                    update(x,*it1-x,0,0,n-1,seg);
                }
                else{
                    update(x,1e9,0,0,n-1,seg);
                }
            }
            else{
                lli x=queries[i][1];
                lli sz=queries[i][2];
                if(x-sz<0){
                    ans.push_back(false);
                    continue;
                }
                lli p=rsum(0,x-sz,0,0,n-1,seg);
                //cout<<i<<" "<<p<<endl;
                if(p>=sz){
                    ans.push_back(true);
                }
                else{
                    ans.push_back(false);
                }
            }
        }
        return ans;
    }
};
