#include <bits/stdc++.h>
using namespace std;
const int N = 50005;
class Solution {
public:
    int f[N];
    void add(int x, int val)
    {
        for(; x < N; x += (x & (-x)))
            f[x] = max(f[x], val);
    } 
    int query(int x)
    {
        int res = 0;
        for(; x; x -= (x & (-x)))
            res = max(res, f[x]);
        return res;   
    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        set <int> s;
        s.insert(0);
        s.insert(50001);
        for(auto x : queries)
            if(x[0] == 1)
            {
                int y = x[1];
                s.insert(y);
            }
        int last = -1;
        for(auto x : s)
        {
            if(last != -1)
            {
                add(x, x - last);
            }
            last = x;
        }
        int n = queries.size();
        vector<bool> ans;
        for(int i = n - 1; i >= 0; i--)
        {
            auto op = queries[i];
            if(op[0] == 1)
            {
                auto p = *--s.lower_bound(op[1]);
                auto q = *++s.lower_bound(op[1]);
                add(q, q - p);
                s.erase(op[1]);
            }
            else
            {
                auto x = *--s.lower_bound(op[1]);
                int res = max(query(x), op[1] - x);
                if(res >= op[2])
                    ans.push_back(true);
                else ans.push_back(false);
            }
        }
        int nn = ans.size();
        for(int i = 0, j = nn - 1; i <= j; i++, j--)
            swap(ans[i], ans[j]);
        return ans;
    }
};
