class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<int> main;
        vector<int> larg;
        vector<bool> ans;

        for (const auto& q : queries) 
        {
            if (q[0] == 1) 
            {
                auto it = lower_bound(main.begin(), main.end(), q[1]);
                int ind = it - main.begin();
                if (ind == 0) 
                {
                    main.insert(main.begin(), q[1]);
                    larg.insert(larg.begin(), q[1]);
                } 
                else 
                {
                    main.insert(main.begin() + ind, q[1]);
                    larg.insert(larg.begin() + ind, max(larg[ind - 1], main[ind] - main[ind - 1]));
                }
                ind++;
                while (ind < main.size()) 
                {
                    if (larg[ind] == larg[ind - 1]) 
                    {
                        break;
                    }
                    larg[ind] = max(larg[ind - 1], main[ind] - main[ind - 1]);
                    ind++;
                }
            } 
            else 
            {
                if (q[2] > q[1]) 
                {
                    ans.push_back(false);
                } 
                else 
                {
                    auto it = lower_bound(main.begin(), main.end(), q[1]);
                    int ind = it - main.begin();
                    int lim;

                    if (ind == 0) 
                    {
                        lim = q[1];
                    } 
                    else if(main[ind - 1] == q[1]) 
                    {
                        lim = larg[ind - 1];
                    } 
                    else 
                    {
                        lim = max(larg[ind-1], q[1] - main[ind - 1]);
                    }
                    ans.push_back(q[2] <= lim);
                }
            }
        }
        return ans;
    }
};
