class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        def check(x,sz):
            key = x//w
            a = 0
            for i in range(key):
                if C[i]>=sz:
                    return True
                if B[i]:
                    if B[i][0]-a>=sz:
                        return True
                    a = B[i][-1]
            pos = bisect_left(B[key],x)-1
            for c,d in pairwise([a]+B[key][:pos+1]+[x]):
                if d-c>=sz:
                    return True
            return False 

        ma = 5*(10**4)+1
        w = isqrt(ma)
        B = defaultdict(list)
        C = defaultdict(int)
        res = []
        for q in queries:
            if q[0]==1:
                x = q[1]
                key = x//w
                B[key].append(x)
                B[key].sort()
                C[key] = max([b-a for a,b in pairwise(B[key])],default=0)
            else:
                res.append(check(q[1],q[2]))
        return res
