class Solution {
public:
    vector<int> a;int N,n;
    void input()
    {
        n=50000;
        N=n;
        if(N & (N - 1))N=0x8000000000000000 >> (__builtin_clzll(N) - 1);
        a=vector<int>(N*2+1,0);
        for(int i=N-1;i>=1;i--)a[i]=max(a[2*i],a[2*i+1]);
    }
    //p=1,lol=N,s,e,ps=1,pe=n
    int query(int p,int lol,int s,int e,int ps,int pe)
    {
        if(pe<=e && ps>=s)return a[p];
        if(pe<s || ps>e)return 0;
        int m=ps+lol/2-1;
        return max(query(p*2,lol/2,s,e,ps,m),query(p*2+1,lol/2,s,e,m+1,pe));
    }
    //p=1,lol=N,ps=1,pe=n,d=destination,x=value
    int pointupdate(int p,int lol,int ps,int pe,int d,int x)
    {
        if(pe<d || ps>d)return a[p];
        if(lol==1)return a[p]=x;
        else return a[p]=max(pointupdate(p*2,lol/2,ps,ps+lol/2-1,d,x),pointupdate(p*2+1,lol/2,ps+lol/2,pe,d,x));
    }
    vector<bool> getResults(vector<vector<int>>& qu) {
        input();
        vector<bool> ans;
        set<int> b;
        b.insert(0);
        for(auto q:qu)
        {
            if(q[0]==1)
            {
                auto it2=b.lower_bound(q[1]);
                auto it1=it2;
                it1--;
                // cout << *it1 << " ";
                // if(it2!=b.end())cout << *it2;
                // cout << endl;
                if(it2!=b.end())
                {
                    pointupdate(1,N,1,n,*it2,*it2-q[1]);
                }
                pointupdate(1,N,1,n,q[1],q[1]-*it1);
                b.insert(q[1]);
            }
            else
            {
                auto it=b.lower_bound(q[1]);it--;
                int ms=max(query(1,N,1,*it,1,n),q[1]-*it);
                ans.push_back((q[2]<=ms));
            }
        }
        return ans;
    }
};
