typedef pair<int, int> pii;
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<int> cur(k+1);
        map<pii, int> dp;
        for (int num : nums) {
            for (int i = 0; i <= k; i++) {
                int& value = dp[make_pair(i, num)];
                value++;
                if (i-1 >= 0) {
                    value = max(value, cur[i-1]+1);
                }
            }
            for (int i = 0; i <= k; i++) {
                cur[i] = max(cur[i], dp[make_pair(i, num)]);
            }
        }
        return *std::max_element(cur.begin(), cur.end());

    }
};
