class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mp[k+1];
        map<int,int> us[k+1];
        int mx=0;
        for(int i=0;i<n;i++)
        {
            for(int j=k;j>=0;j--)
            {
                int x=nums[i],ans=1;
                //mp[j][x]
                if(true) {
                    ans=mp[j][x]+1;
                }
                if(j) {
                    int cnt=0;
                    for(auto val:us[j-1]) {
                        cnt++;
                        ans=max(ans,(-val.first)+1);
                        if(cnt==2)break;
                    }
                }
                us[j][-ans]=x;
                mp[j][x]=ans;
                mx=max(mx,ans);
            }
        }
        return mx;
    }
};

