class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        allScore = {} # (digit, alternations) -> score
        bestScore = {} # alternations -> score

        for alt in range(k+1):
            bestScore[alt] = 0
            for num in nums:
                allScore[(num, alt)] = 0

        for num in nums:
            bestScoreOld = bestScore.copy()
            for alt in range(k+1):
                allScore[(num, alt)] += 1
                if alt > 0 and bestScoreOld[alt-1] >= allScore[(num, alt)]:
                    allScore[(num, alt)] = bestScoreOld[alt-1] + 1
                if allScore[(num, alt)] > bestScoreOld[alt]:
                    bestScore[alt] = allScore[(num, alt)]
            #print(num, bestScore)
            #print(allScore)

        return max(bestScore.values())
