class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        temp=[]
        Hash={}
        kmax=[0]*(k+1)
        cnt=0
        for i in nums:
            if i not in Hash:
                Hash[i]=cnt
                cnt+=1
                temp.append([1])
                for j in kmax[0:k]:
                    temp[-1].append(j+1)
                for j in range(k+1):
                    kmax[j]=max(kmax[j],temp[-1][j])
            else:
                index=Hash[i]
                temp[index][0]+=1
                for j in range(k):
                    temp[index][j+1]=max(temp[index][j+1]+1,kmax[j]+1)
                for j in range(k+1):
                    kmax[j]=max(kmax[j],temp[index][j])
        return max(kmax)
