class Solution:
    def maximumLength(self, nums: List[int], K: int) -> int:
        best = defaultdict(int)
        heap = defaultdict(lambda : -inf)
        heap[K] = 0
        for i in range(len(nums) - 1, -1, -1):
            for k in range(K + 1):
                best[nums[i], k] = max(1 + best[nums[i], k], 1 + heap[k + 1])
                heap[k] = max(best[nums[i], k], heap[k])
        return max(heap.values())
