class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        best = [0] * (k + 1)
        d = defaultdict(lambda: [0] * (k + 1))
        for x in nums:
            l = d[x]
            l[0] += 1
            for i in range(1, k + 1):
                l[i] = max(l[i], best[i - 1]) + 1
            for i in range(k + 1):
                best[i] = max(best[i], l[i])
        # print(d, best)
        return max(best)
