class Solution {
public:
    int maximumLength(vector<int>& A, int k) {
        veci cc(k + 1, 0);
        map<int, veci> len;
        ll res = 0;
        rep(i, A.size()){
            if(!len.contains(A[i])) len[A[i]] = veci(k + 1, 0);
            veci& cur = len[A[i]];
            rep(j, k + 1) cur[j]++;
//            for(int j = k; j > 0 ; --j) chmax(cc[j], cc[j - 1] + 1);
            for(int j = k; j > 0 ; --j) chmax(cur[j], cc[j - 1] + 1);
            rep(j, k + 1) chmax(cc[j], cur[j]);
            rep(j, k + 1){
                chmax(res, cur[j]);
                chmax(res, cc[j]);
            }
        }

        return res;
    }
};
