class Solution {
    public int maximumLength(int[] nums, int k) {
        int n= nums.length;
        int [][]dp = new int[n][k+2];

        int max[] = new int[k+2];

        int ans = 0;
        HashMap<Integer, Integer> hm = new HashMap<>();
        for(int i = 0 ; i < n ; i++){
            for(int j = 1 ; j <= k+1 ; j ++){
                if(j == 0){
                    if(hm.containsKey(nums[i])){
                        int index = hm.get(nums[i]);
                        dp[i][j] = Math.max(dp[i][j], dp[index][j]+1);
                    }
                    else{
                        dp[i][j] = Math.max(dp[i][j], 1);
                    }
                }
                else{
                    if(hm.containsKey(nums[i])){
                        int index = hm.get(nums[i]);
                        dp[i][j] = Math.max(dp[i][j], Math.max(dp[index][j]+1, max[j-1]+1));
                    }
                    else{
                        dp[i][j] = Math.max(dp[i][j], max[j-1]+1);
                    }
                }
                ans = Math.max(ans, dp[i][j]);
            }
            for(int j = 1 ; j <= k+1 ; j++){
                max[j] = Math.max(max[j], dp[i][j]);
            }
            hm.put(nums[i], i);
        }

        // for(int i = 0 ; i < n ; i++){
        //     for(int j = 1; j <= k+1 ; j++){
        //         System.out.print(dp[i][j] + " ");
        //     }
        //     System.out.println();
        // }
        return ans;
    }
}
