class Solution {
public:
    int maximumLength(vector<int>& nums, int K) {
        int n = nums.size();

        vector<vector<int>> dp(n, vector<int> (K+1, 0));

        for(int k = 0; k <= K; ++k){

            int mx1 = INT_MIN, mx2 = INT_MIN, mx1num = -1, mx2num = -1;
            unordered_map<int, int> mp;

            for(int i = 0; i < n; ++i){
                dp[i][k] = (k == 0 ? 1 : dp[i][k-1]);

                dp[i][k] = max(dp[i][k], mp[nums[i]] + 1);

                if(k > 0){
                    if(nums[i] == mx1num){
                        dp[i][k] = max(dp[i][k], mx2 + 1);
                    }else{
                        dp[i][k] = max(dp[i][k], mx1 + 1);
                    }
                }

                mp[nums[i]] = max(mp[nums[i]], dp[i][k]);

                if(k > 0){
                    if(dp[i][k-1] > mx1){
                        mx2 = mx1;
                        mx2num = mx1num;
                        mx1 = dp[i][k-1];
                        mx1num = nums[i];
                    }else if(dp[i][k-1] > mx2){
                        mx2 = dp[i][k-1];
                        mx2num = nums[i];
                    }
                }

            }

        }

        int ans = INT_MIN;
        for(int i = 0; i < n; ++i){
            ans = max(ans, dp[i][K]);
        }

        return ans;
    }
};
