typedef long long ll;
class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<ll>> dp(n,vector<ll> (k+1,0));
        map<ll,ll> las;
        vector<ll> lo(k+1,0);
        for(int i=0;i<n;i++){
            int t=las[a[i]];
            if(t){
                t--;
                for(int j=0;j<=k;j++){
                    dp[i][j]=1+dp[t][j];
                }
            }
            dp[i][0]=max(dp[i][0],1LL);
            for(int j=0;j<=k;j++){
                dp[i][j]=max(dp[i][j],lo[j]+1);
            }
            for(int j=0;j<k;j++){
                //cout<<lo[j+1]<<" ";
                lo[j+1]=max(lo[j+1],dp[i][j]);
            }
            //cout<<endl;
            las[a[i]]=i+1;
        }
       // cout<<"||"<<endl;
        ll ans=0;
        for(auto &i:dp){
            for(auto &j:i){ //cout<<j<< " " ;
            ans=max(ans,j);}
            //cout<<endl;
        }
        return (int)ans;
    }
};
