class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(k + 1, vector<int>(n + 2, 0));
        for (int i = 0; i <= k; i++) {
            map<int, int> mp;
            int mx = 0;
            for (int j = 0; j < n; j++) {
                dp[i][j] = mp[nums[j]] + 1;
                if (i) {
                    dp[i][j] = max(dp[i][j], 1 + mx);
                    mx = max(mx, dp[i - 1][j]);
                }
                mp[nums[j]] = dp[i][j];
            }
        }
        return *max_element(dp[k].begin(), dp[k].end());
    }
};
