class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>>dp(n,vector<int>(k+1));
        vector<int>mx(k+1);
        map<int,int>best[k+1];
        int ans=1;
        for(int i=0;i<n;i++){
            for(int j=k;j>=1;j--){
                //from different
                int can=mx[j-1];
                dp[i][j]=can+1;
                //from same
                can=best[j][a[i]];
                dp[i][j]=max(dp[i][j],can+1);
                mx[j]=max(mx[j],dp[i][j]);
                best[j][a[i]]=max(best[j][a[i]],dp[i][j]);
                ans=max(ans,dp[i][j]);
            }
            //from same
            dp[i][0]=1;
            dp[i][0]=max(dp[i][0],best[0][a[i]]+1);
            mx[0]=max(mx[0],dp[i][0]);
            best[0][a[i]]=max(best[0][a[i]],dp[i][0]);
            ans=max(ans,dp[i][0]);
        }
        return ans;
    }
};
