class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        int dp[n][k+1];
        int ans=0;
        int a=0;
        map<int,int>mp;
        for(int j=0;j<=k;j++)
        {
            mp.clear();
            a=0;
            for(int i=0;i<n;i++)
            {
                dp[i][j]=1;
                dp[i][j]=max({dp[i][j],a+1,mp[nums[i]]+1});

                ans=max(ans,dp[i][j]);

                mp[nums[i]]=max(mp[nums[i]],dp[i][j]); 
                if(j!=0)
                {
                    a=max(a,dp[i][j-1]);
                }
            }
        }
        // for(int i=0;i<n;i++)
        // {
        //     for(int j=0;j<=k;j++) cout<<dp[i][j]<<" ";
        //     cout<<endl;
        // }
        return ans;

    }
};
