class Solution {
public:
    int maximumLength(vector<int>& a, int kk) {
        vector<map<int, int>> dp(55);
        vector<int> gl(55, INT_MIN);
        int n = a.size();
        for(int i = 0; i < n; i++){
            for(int j = 54; j > -1; j--){
                auto it = dp[j].find(a[i]);
                if(it != dp[j].end()){
                    (*it).second++;
                    gl[j] = max(gl[j], (*it).second);
                }
                if(j){
                    dp[j][a[i]] = max(dp[j][a[i]], gl[j - 1] + 1);
                    gl[j] = max(gl[j], dp[j][a[i]]);
                }else{
                    dp[0][a[i]] = max(dp[0][a[i]], 1);
                    gl[0] = max(gl[0], 1);
                }
            }
        }
        // for(int i = 0; i < n; i++){
        //     for
        // }


        // dp[0][0] = 1;
        // for(int i = 0; i < n; i++){
        //     dp[i][0] = 1;
        //     for(int j = 0; j < i; j++){
        //         if(a[j] == a[i]){
        //             for(int k = 0; k < 30; k++){
        //                 dp[i][k] = max(dp[i][k], dp[j][k] + 1);
        //             }
        //         }else{
        //             for(int k = 0; k < 29; k++){
        //                 dp[i][k + 1] = max(dp[i][k + 1], dp[j][k] + 1);
        //             }
        //         }
        //     }
        // }
        int ans = 0;
        for(int i = 0; i < n; i++){
            for(int j = 0; j <= kk; j++){
                ans = max(ans, dp[j][a[i]]);
            }
        }
        return ans;
    }
};
