class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> maxi(k+1,-1);
        map<int,int> m;
        vector<vector<int>> dp(n,vector<int>(k+1,0));
        for(int i=0;i<nums.size();i++){
            dp[i][0]=1;
            for(int j=1;j<=k;j++){
                dp[i][j]=max(maxi[j-1]+1,dp[i][j]);
            }
            if(m.find(nums[i])!=m.end()){
                int j=m[nums[i]];
                for(int l=0;l<=k;l++){
                    dp[i][l]=max(dp[i][l],dp[j][l]+1);
                }
            }
            m[nums[i]]=i;
            for(int j=0;j<=k;j++){
                maxi[j]=max(maxi[j],dp[i][j]);
            }
        }

        int ans=0;
        for(int i=0;i<nums.size();i++){
            for(int j=0;j<=k;j++){
                ans=max(ans,dp[i][j]);
            }
        }
        return ans;
    }
};
