class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> prematch(n,-1);
        map<int,int> m;
        for(int i=0;i<n;i++){
            if(m.find(nums[i])!=m.end()){
                prematch[i]=m[nums[i]];
            }
            m[nums[i]]=i;
        }
        vector<int> kmaxss(k+1,1);
        vector<vector<int>> dp(n, vector<int> (k+1,-1));
        for(int j=0;j<=k;j++) dp[0][j]=1;
        for(int i=1;i<n;i++){
            for(int j=0;j<=k;j++){
                int ans=1;
                if(j!=0) ans=max(ans,1+kmaxss[j-1]);
                if(prematch[i]!=-1) ans=max(ans,1+dp[prematch[i]][j]);
                dp[i][j]=ans;
            }
            for(int j=0;j<=k;j++){
                kmaxss[j]=max(kmaxss[j],dp[i][j]);
            }
        }
        int ans=1;
        for(int i=0;i<n;i++){
            ans=max(ans,dp[i][k]);
        }
        return ans;
    }







