class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        poss = defaultdict(int)
        n = len(nums)
        dp = [[1 for j in range(n)] for i in range(k + 1)]
        maxis = [0 for i in range(k + 1)]
        for i in range(n):
            for kk in range(k + 1):
                if nums[i] in poss:
                    dp[kk][i] = max(dp[kk][i], 1 + dp[kk][poss[nums[i]]])
                if kk > 0:
                    dp[kk][i] = max(dp[kk][i], 1 + maxis[kk-1])
            for kk in range(k + 1):
                maxis[kk] = max(maxis[kk], dp[kk][i])
            poss[nums[i]] = i
        return max(dp[-1][i] for i in range(n))
