class Solution {
public:
    int maximumLength(vector<int>& arr, int k) {
        int n= arr.size();

        int dp[n][k+1], abs[n][k+1];
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                dp[i][j] = 0;
                abs[i][j] = 0;
            }
        }
        map<int,int> recent;
        for(int i=n-1;i>=0;i--){

            // for(auto x: recent){
            //     cout<<x.first<<" "<<x.second<<endl;
            // }
            int maxLen = 0;
            for(int j=0;j<=k;j++){
                  if(i==n-1){
                      dp[i][j]         = max(dp[i][j],1);
                      abs[i][j] = 1;

                  } else {

                      if(recent.find(arr[i]) != recent.end()){
                        abs[i][j] = max(abs[i][j], 1+abs[recent[arr[i]]][j]);
                        dp[i][j] = max(dp[i][j], 1+abs[recent[arr[i]]][j]);
                      } 

                      if(j>0){
                          dp[i][j] = max(dp[i][j], 1+dp[i+1][j-1]);
                          abs[i][j] = max(abs[i][j], 1+dp[i+1][j-1]);
                      } else {
                          dp[i][j]         = max(dp[i][j],1);
                          abs[i][j]         = max(abs[i][j],1);
                      }   
                  }
                  if(i+1<n)
                  dp[i][j] = max( dp[i+1][j], dp[i][j]);

                // cout<<dp[i][j]<<" ";
            }
            recent[arr[i]] = i;
            // cout<<endl;

        }

//         for(int i=0;i<n;i++){
//             for(int j=0;j<=k;j++)
//                 cout<<dp[i][j]<<" ";
//             cout<<endl;
//         }
//         cout<<endl;
//         for(int i=0;i<n;i++){
//             for(int j=0;j<=k;j++)
//                 cout<<abs[i][j]<<" ";
//             cout<<endl;
//         }



        return dp[0][k];
    }
};
