from collections import Counter

class Solution(object):
    def maximumLength(self, arr, k):
        dp = {i: [0, set(), Counter()] for i in range(k + 1)}

        for i in arr:
            prev = 0
            for j in range(k + 1):
                cur = max(prev + 1, (dp[j][0] + 1) * (i in dp[j][1]), dp[j][2][i] + 1)
                prev, dp[j][2][i] = dp[j][0], cur

                if dp[j][0] < cur:
                    dp[j][:] = [cur, set(), dp[j][2]]

                if cur == dp[j][0]:
                    dp[j][1].add(i)

        return dp[k][0]

