class Solution {
public:
    int maximumLength(vector<int>& v, int k) {
        int n=v.size();
        vector<vector<int>> dp(k+5,vector<int>(n+5,0));
        for(int j=0;j<=k;j++)
        {

          map<int,int> mp;
            int temp=0;
          for(int i=n-1;i>=0;i--)
          {

             mp[v[i]]++;
              if(j==0)
              {
                  temp=max(temp,mp[v[i]]);
                  dp[j][i]=temp;
                  continue;
               }
             mp[v[i]]=max(mp[v[i]],dp[j-1][i+1]+1);
              temp=max(temp,mp[v[i]]);
             dp[j][i]=temp;
          }
        }
        int maxi=0;
        for(int j=0;j<n;j++)
        {
            //cout<<dp[i][j]<<" ";
            maxi=max(maxi,dp[k][j]);
        }

        return maxi;
    }
};
