class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size(),ans=0;
        vector<vector<int>> dp(k+1,vector<int>(n+1,1));
        for(int i=0;i<=k;i++) {
            int mx=0;
            map<int,int> mp;
            for(int j=1;j<=n;j++) {
                dp[i][j]=1+max(mp[nums[j-1]],mx);
                mp[nums[j-1]]=dp[i][j];
                mx=max(mx,(i>0?dp[i-1][j]:0));
                ans=max(ans,dp[i][j]);
            }
        }
        return ans;
    }
};
