class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<int> maxi(51,0);
        int n=nums.size();
        vector<map<int,int>> v(51);
        for(int j=0;j<n;j++){

            for(int i=50;i>=0;i--){
                v[i][nums[j]]++;
                if(i!=0){

                v[i][nums[j]]=max(v[i][nums[j]],maxi[i-1]+1);
                }
                maxi[i]=max(maxi[i],v[i][nums[j]]);

            }
        }
        int ans=1;
        for(auto it:v[k]){
            ans=max(ans,it.second);

        }
        return ans;


    }
};
