class Solution:
    def maximumLength(self, nums: List[int], x: int) -> int:
        dp = defaultdict(int)
        maxes = [0] * (x + 1)
        for c in nums:
            for k in reversed(range(x + 1)):
                if k == 0:
                    dp[(c, 0)] += 1
                    maxes[0] = max(maxes[0], dp[(c, 0)])
                    continue

                # if dp[(c, k)] == 0 and maxes[k - 1] == 0:
                #     continue

                dp[(c, k)] = max(dp[(c, k)] + 1, maxes[k - 1] + 1)
                maxes[k] = max(maxes[k], dp[(c, k)])

                # print(c, k, dp[(c, k)])
        # print(dp, maxes)
        return max(maxes)
