class Solution {
    int dp[5005][51];
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<map<int,int>>mp(k+1);
        vector<vector<int>>om(n,vector<int>(k+1,0));

        for(int i=n-1;i>=0;i--)
        {

            for(int j=0;j<=k;j++)
            {
                dp[i][j]=mp[j][nums[i]]+1;
                if(j!=0 && i!=n-1)
                dp[i][j]=max(dp[i][j],om[i+1][j-1]+1);
                om[i][j]=max(om[i][j],dp[i][j]);
                if(i!=n-1)om[i][j]=max(om[i+1][j],om[i][j]);
                mp[j][nums[i]]=max(dp[i][j],mp[j][nums[i]]);
            }
        }
        int ans=0;
        for(int i=0;i<n;i++)ans=max(ans,dp[i][k]);
        return ans;
    }
};
