class Solution {
public:
    int dp[5001][51];
    int maximumLength(vector<int>& arr, int k) {
                int n = arr.size();
        if(n==1)return 1;
        for(int i=0;i<5001;i++){
            for(int j=0;j<51;j++){
                dp[i][j] = 1;
            }
        }
        map<int,int> mp;
        mp[arr[0]] = 0;
        vector<int> mm(k+1,1);
        for(int i=1;i<n;i++){
            if(mp.find(arr[i])!=mp.end()){
                for(int z=0;z<=k;z++){
                    dp[i][z] = max(dp[i][z],dp[mp[arr[i]]][z]+1);
                }
            }
            for(int z=1;z<=k;z++){
                dp[i][z] = max(dp[i][z],mm[z-1]+1);
            }
            mp[arr[i]] = i;
            for(int z=0;z<=k;z++){
                mm[z] = max(mm[z],dp[i][z]);
            }
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<=k;j++){
        //         cout << dp[i][j] << " ";
        //     }cout << endl;
        // }
        int ans = 0;
        for(int i=0;i<n;i++){
            ans = max(ans,dp[i][k]);
        }
        return ans;
    }
};
