class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

        vector<ll>vec(k+1,0);
        map<ll,map<ll,ll>> mp;
        for(auto &i:nums)
        {
            map<ll,ll> tp;
            tp[0]=1;
            if(mp.count(i))
            {
                for(auto &[a,b]:mp[i])
                {
                    tp[a]=b+1;
                }
            }
            loop(i,0,k)
            {
                tp[i+1]=max(tp[i+1],vec[i]+1);
            }
            mp[i]=tp;
            for(auto &[a,b]:tp)
            {

                vec[a]=max(vec[a],b);
            }

        }
        ll ans=0;
        for(auto &i:vec) ans=max(i,ans);



        return ans;
    }
};
