class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[1 for _ in range(k + 1)] for _ in range(n)]
        ans = 1
        kk = [-1 for _ in range(k + 1)]
        arr = [{} for _ in range(k + 1)]
        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    dp[i][j] = max(dp[i][j], 1 + kk[l])
                if nums[i] in arr[j]:
                    dp[i][j] = max(dp[i][j], 1 + arr[j][nums[i]])
                ans = max(ans, dp[i][j])
            for j in range(k + 1):
                kk[j] = max(kk[j], dp[i][j])
                arr[j][nums[i]] = max(arr[j].get(nums[i], 0), dp[i][j])
        return ans
