const int N=5e3+10;
int dp[N][51];
int maxi[51];
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
         int n=nums.size(), ans = 0;
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++)dp[i][j]=1;
        }
        map<int,int> mp;
        memset(maxi,0,sizeof(maxi));
        for (int i = 0; i < n; i++) {
            for (int j = k; j >= 0; j--) {
                if (j >= 1) {
                    dp[i][j] = maxi[j - 1] + 1;
                }
                if (mp.find(nums[i]) != mp.end()) {
                    dp[i][j] = max(dp[i][j], dp[mp[nums[i]]][j] + 1);
                }
                maxi[j] = max(maxi[j], dp[i][j]);
                ans = max(ans, dp[i][j]);
            }
            mp[nums[i]] = i;
        }
        return ans;

    }
};
