#define mp(a, b) make_pair(a, b)
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        map<pair<int, int>, int> dp;
        map<int, int> dp2;
        for(int i = 0; i <= k; i++)
        {
            dp[mp(nums[0], i)] = 1;
            dp2[i] = 1;
        }
        int res = 1;
        for(int i = 1; i < n; i++)
        {
            for(int j = k; j >= 0; j--)
            {
                if(j == 0)
                {
                    dp[mp(nums[i], j)]++;
                }
                else
                {
                    dp[mp(nums[i], j)] = max(dp[mp(nums[i], j)] + 1, dp2[j-1] + 1);
                }
                dp2[j] = max(dp2[j], dp[mp(nums[i], j)]);
                res = max(res, dp[mp(nums[i], j)]);
            }
        }
        return res;
    }
};
