class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[0 for _ in range(k+1)] for _ in range(n)]
        dp[0][0]=1
        d = {}
        lasts = {nums[0]:0}
        for i in range(k+1):
            d[i]=[0,-1]
        d[0]=[1,nums[0]]
        mx = 1
        for i in range(1,n):
            x = nums[i]
            dp[i][0]=1
            for l in range(k+1):
                if x in lasts:
                    dp[i][l] = max(dp[i][l],dp[lasts[x]][l]+1)
                if l>0:
                    dp[i][l] = max(dp[i][l],d[l-1][0]+1)
                if l>0 and dp[i][l-1]>d[l-1][0]:
                    d[l-1]=[dp[i][l-1],x]
                if l==k and dp[i][l]>d[l][0]:
                    d[l]=[dp[i][l],x]

                mx = max(mx,dp[i][l])
            lasts[x]=i
        return mx
