class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        unordered_map<int,vector<int>>dp;
        for(int i:nums)dp[i]=vector<int>(k+1);
        vector<int>cols(k+1,0);
        for(int i:nums){
            dp[i][0]++;
            for(int j=1;j<=k;j++){
                dp[i][j]=max(dp[i][j],cols[j-1]);
                dp[i][j]++;
            }
            for(int j=0;j<k;j++)cols[j]=max(cols[j],dp[i][j]);
        }
        int res=0;
        for(int i:nums){
            res=max(res,dp[i][k]);
        }
       /* for(auto it=dp.begin();it!=dp.end();it++){
            cout<<(*it).first<<": ";
            print((*it).second);
        }*/
        return res;
    }
};
