class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        dp = [[0, defaultdict(int)] for _ in range(k+1)]
        # Represents [max(c.values), c=Counter] for each k
        for i in nums:
            for n in range(k, 0, -1):
                dp[n][1][i] = max(dp[n][1][i]+1, dp[n-1][0]+1)
                dp[n][0] = max(dp[n][0], dp[n][1][i])
            dp[0][1][i] += 1
            dp[0][0] = max(dp[0][0], dp[0][1][i])
            # print(dp)
        return max(i[0] for i in dp)
