class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int n=a.size();
        int ans=1;
        int dp[n][k+1];
        for(int i=0;i<n;i++)for(int j=0;j<=k;j++)dp[i][j] = 1;

        map<int,int> b;
        for(int i=0;i<n;i++)b[i] = 0;

        for(int i=0;i<=k;i++)
        {
            map<int,int> mp;
            for(int j=0;j<n;j++)
            {
                if(j == 0)
                {
                    mp[a[j]] = max(mp[a[j]],dp[j][i]);
                    continue;
                }

                dp[j][i] = 1 + max(mp[a[j]] , b[j-1]);

                mp[a[j]] = max(mp[a[j]],dp[j][i]);

                    ans=max(ans,dp[j][i]);
            }
            int ma=dp[0][i];
            for(int j=1;j<n;j++)
            {
                b[j-1] = max(b[j-1] , ma);
                ma=max(dp[j][i],ma);
            }
        }

        return ans;
    }
};
