class Solution {
public:
    int maximumLength(vector<int>& nums, int k)
    {
        int n = nums.size();

        vector<vector<int>> dp(n, vector<int>(k+1, 0));

        // k = 0
        dp[0][0] = 1;
        map<int,int> M;
        M[nums[0]] = 1;
        for(int i = 1; i < n; i++)
        {
            dp[i][0] = max(1, 1 + M[nums[i]]);
            M[nums[i]] = max(M[nums[i]], dp[i][0]);

            // for(int j = i-1; j >=0; j--)
            // {
            //     if(nums[i] == nums[j])
            //         dp[i][0] = max(dp[i][0], 1+dp[j][0]);
            // }
        }

        // all k
        for(int p = 1; p < k+1; p++)
        {
            dp[0][p] = 1;
            map<int,int> M;
            M[nums[0]] = 1;
            int val = dp[0][p-1];
            for(int i = 1; i < n; i++)
            {
                dp[i][p] = 1;
                dp[i][p] = max(dp[i][p], 1+M[nums[i]]);
                dp[i][p] = max(dp[i][p], 1+val);

                M[nums[i]] = max(M[nums[i]], dp[i][p]);
                val = max(val, dp[i][p-1]);
                // for(int j = i-1; j >=0; j--)
                // {
                //     if(nums[i] == nums[j])
                //         dp[i][p] = max(dp[i][p], 1+dp[j][p]);
                //     else
                //         dp[i][p] = max(dp[i][p], 1+dp[j][p-1]);
                // }
            }
        }

        int ans = 1;
        for(int i = 0; i < n; i++)
            ans = max(ans, dp[i][k]);

        return ans;
    }
};
