class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n , res = len(nums), 1
        dp = [[1] * (k + 1) for _ in range(n)]
        arr = [-1] * (k + 1)
        mp = [defaultdict(int) for _ in range(k + 1)]
        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    dp[i][j] = max(dp[i][j], 1 + arr[l])
                if nums[i] in mp[j]:
                    dp[i][j] = max(dp[i][j], 1 + mp[j][nums[i]])
                res = max(res, dp[i][j])
            for j in range(k+1):
                arr[j]=max(arr[j], dp[i][j])
                mp[j][nums[i]] = max(mp[j][nums[i]], dp[i][j])

        return res
