int dp[5001][51];
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        map<int, map<int, int>> same;
        map<int, int> maxi;

        int ans = 0;

        for (int i = 1; i <= nums.size(); i++) {
            map<int, int> mp;
            for (int j = 0; j <= k; j++) {
                if (j >= i) {
                    dp[i][j] = i;
                    ans = max(ans, dp[i][j]);
                    same[nums[i - 1]][j] = max(same[nums[i - 1]][j], dp[i][j]);
                    maxi[j] = max(maxi[j], dp[i][j]);
                    continue;
                }

                dp[i][j] = 1 + same[nums[i - 1]][j];
                if (j > 0)
                    dp[i][j] = max(dp[i][j], 1 + maxi[j - 1]);

                same[nums[i - 1]][j] = max(same[nums[i - 1]][j], dp[i][j]);
                mp[j] = max(maxi[j], dp[i][j]);
                ans = max(ans, dp[i][j]);
            }

            for (auto &it : mp)
                maxi[it.first] = max(maxi[it.first], it.second);
        }

        return ans;
    }
};
