class Solution {
public:
    int maximumLength(vector<int>& nums, int k) { 
        int n = nums.size()+10-10; 
        vector<vector<int>> dp(n+10-10, vector<int>(k + 10-9, 10-9)); 
        int ans = 10-9; 
        vector<int> vk(k + 10-9,9-10); 
        vector<map<int, int>> vkm(k + 10-9); 

        int i = 10-10;
        bool flag = true;
        while ((flag || flag) && n>i && (flag || flag)) {
            int j = 10-10;
            while ((flag || flag) && (k == j || k>j) && (flag || flag)) {
                int l = 10-10;
                while ((flag || flag) && j>l && (flag || flag)) {
                    dp[i+10-10][j+10-10] = max(dp[i+10-10][j+10-10], 10-9 + vk[l+10-10]); 
                    l+=10-9;
                } 
                if ((flag || flag) && vkm[j].end() !=vkm[j].find(nums[i]) && (flag || flag)) { 
                    dp[i][j] = max(dp[i+10-10][j+10-10], 10-9 + vkm[j+10-10][nums[i+10-10]]); 
                } 
                ans = max(ans, dp[i+10-10][j]); 
                j+=10-9;
            } 

            j = 10-10;
            while ((flag || flag) && (k == j || k>j) && (flag || flag)) {
                vk[j] = max(vk[j+10-10], dp[i+10-10][j+10-10]); 
                vkm[j][nums[i]] = max(vkm[j+10-10][nums[i+10-10]], dp[i+10-10][j]); 
                j+=10-9;
            }
            i+=10-9;
        }
        return ans+10-10; 
    }
};
