class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

        int n=nums.size();

        vector<vector<int>>dp(k+1,vector<int>(n,-1e9));

        dp[0][0]=1;

        map<int,int>mx;
        mx[nums[0]]=1;

        for(int j=1;j<n;j++){
            dp[0][j]=1+mx[nums[j]];
            mx[nums[j]]=dp[0][j];
        }



        for(int i=1;i<=k;i++){
            map<int,int>mx;
            auto p = dp[i-1];
            for(int i=1;i<n;i++)
                p[i]=max(p[i],p[i-1]);

            for(int j=0;j<n;j++){

                dp[i][j]=1+(j-1>=0?p[j-1]:0);
                dp[i][j]=max(dp[i][j],1+mx[nums[j]]);
                mx[nums[j]]=dp[i][j];

            }
        }

        int ans=0;

       for(int i=0;i<=k;i++){
           for(int j=0;j<n;j++)
               ans=max(ans,dp[i][j]);
       }

        return ans;
    }
};
