class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        int dp[n+1][k+1][2];
        map<int,int> m;
        for(int i=0;i<=n;i++){
            for(int j=0;j<=k;j++){
                dp[i][j][0]=1;
                dp[i][j][1]=1;
            }
        }
        int maxx=0;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=k;j++){
                if(j>0){
                    dp[i][j][0]=dp[i][j-1][0];
                    dp[i][j][1]=dp[i][j-1][1];
                    if(i>1)
                        dp[i][j][1]=dp[i-1][j-1][0]+1;
                }
                dp[i][j][0]=max(dp[i][j][0],dp[i-1][j][0]);
                if(m[nums[i-1]]!=0){
                    dp[i][j][1]=max(dp[i][j][1],dp[m[nums[i-1]]][j][1]+1);
                }
                dp[i][j][0]=max(dp[i][j][0],dp[i][j][1]);
                maxx=max(maxx,dp[i][j][0]);
            }
            m[nums[i-1]]=i;
        }
        return maxx;
    }
};
